#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
}
$ret=0
if (Test-Path "$basedir//home/karan/.nvm/versions/node/v21.7.1/bin/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir//home/karan/.nvm/versions/node/v21.7.1/bin/node$exe"  "$basedir/../karan/index.js" $args
  } else {
    & "$basedir//home/karan/.nvm/versions/node/v21.7.1/bin/node$exe"  "$basedir/../karan/index.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "/home/karan/.nvm/versions/node/v21.7.1/bin/node$exe"  "$basedir/../karan/index.js" $args
  } else {
    & "/home/karan/.nvm/versions/node/v21.7.1/bin/node$exe"  "$basedir/../karan/index.js" $args
  }
  $ret=$LASTEXITCODE
}
exit $ret
